form.addEventListener('submit', function (e) {
  e.preventDefault()
  checkInput([username, email, password, confirmPassword])
  checkLength(username, 3, 8)
  checkLength(password, 3, 12)
  checkMail(email)
  checkPasswordMatch(password, confirmPassword)
})
